import React, { useState } from "react";
import { Link } from "react-router-dom";
import styles from "../styles/Navbar.module.css";
import "bootstrap/dist/css/bootstrap.min.css";
import AuthForm from "./AuthForm";

const Navbar: React.FC = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);

  const handleAuthClick = () => {
    setIsAuthModalOpen(true);
  };

  const handleCloseAuth = () => {
    setIsAuthModalOpen(false);
  };

  return (
    <>
      <nav className={`navbar navbar-expand-lg ${styles.navbar}`}>
        <div className="container">
          <Link className={`navbar-brand ${styles.navbarBrand}`} to="/">
            <i className="bi bi-heart-fill"></i>
            VolunteerHub
          </Link>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto">
              <li className="nav-item">
                <Link className={`nav-link ${styles.navLink}`} to="/">
                  Головна
                </Link>
              </li>
              <li className="nav-item">
                <Link className={`nav-link ${styles.navLink}`} to="/events">
                  Події
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${styles.navLink}`}
                  to="/organizations"
                >
                  Організації
                </Link>
              </li>
            </ul>

            <div className="d-flex">
              <input
                className={`form-control me-2 ${styles.searchInput}`}
                type="search"
                placeholder="Пошук подій..."
                aria-label="Search"
              />
              <button
                className="btn btn-outline-success me-2"
                onClick={handleAuthClick}
              >
                Увійти
              </button>
              <Link className="btn btn-success" to="/profile">
                <i className="bi bi-person me-2"></i>
                Профіль
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {isAuthModalOpen && <AuthForm onClose={handleCloseAuth} />}
    </>
  );
};

export default Navbar;
