import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styles from "../styles/Navbar.module.css";
import "bootstrap/dist/css/bootstrap.min.css";
import AuthForm from "./AuthForm";
import { authService } from "../services/api";
import { User } from "../types/api";

const Navbar: React.FC = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const currentUser = await authService.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        setUser(null);
      } finally {
        setIsLoading(false);
      }
    };

    checkAuth();
  }, []);

  const handleAuthClick = () => {
    setIsAuthModalOpen(true);
  };

  const handleCloseAuth = () => {
    setIsAuthModalOpen(false);
  };

  const handleLogout = () => {
    authService.logout();
    setUser(null);
  };

  return (
    <>
      <nav className={`navbar navbar-expand-lg ${styles.navbar}`}>
        <div className="container">
          <Link className={`navbar-brand ${styles.navbarBrand}`} to="/">
            <i className="bi bi-heart-fill"></i>
            VolunteerHub
          </Link>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto">
              <li className="nav-item">
                <Link className={`nav-link ${styles.navLink}`} to="/">
                  Головна
                </Link>
              </li>
              <li className="nav-item">
                <Link className={`nav-link ${styles.navLink}`} to="/events">
                  Події
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${styles.navLink}`}
                  to="/organizations"
                >
                  Організації
                </Link>
              </li>
            </ul>

            <div className="d-flex">
              <input
                className={`form-control me-2 ${styles.searchInput}`}
                type="search"
                placeholder="Пошук подій..."
                aria-label="Search"
              />
              {!isLoading && (
                <>
                  {user ? (
                    <div className="dropdown">
                      <button
                        className="btn btn-success dropdown-toggle"
                        type="button"
                        id="userDropdown"
                        data-bs-toggle="dropdown"
                        aria-expanded="false"
                      >
                        <i className="bi bi-person me-2"></i>
                        {user.name}
                      </button>
                      <ul
                        className="dropdown-menu dropdown-menu-end"
                        aria-labelledby="userDropdown"
                      >
                        <li>
                          <Link className="dropdown-item" to="/profile">
                            <i className="bi bi-person me-2"></i>
                            Профіль
                          </Link>
                        </li>
                        <li>
                          <Link className="dropdown-item" to="/my-events">
                            <i className="bi bi-calendar-event me-2"></i>
                            Мої події
                          </Link>
                        </li>
                        <li>
                          <hr className="dropdown-divider" />
                        </li>
                        <li>
                          <button
                            className="dropdown-item text-danger"
                            onClick={handleLogout}
                          >
                            <i className="bi bi-box-arrow-right me-2"></i>
                            Вийти
                          </button>
                        </li>
                      </ul>
                    </div>
                  ) : (
                    <button
                      className="btn btn-outline-success me-2"
                      onClick={handleAuthClick}
                    >
                      Увійти
                    </button>
                  )}
                </>
              )}
            </div>
          </div>
        </div>
      </nav>

      {isAuthModalOpen && <AuthForm onClose={handleCloseAuth} />}
    </>
  );
};

export default Navbar;
