import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styles from "../styles/Navbar.module.css";
import "bootstrap/dist/css/bootstrap.min.css";
import AuthForm from "./AuthForm";
import { authService } from "../services/api";
import { User } from "../types/api";

const Navbar: React.FC = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const currentUser = await authService.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        setUser(null);
      } finally {
        setIsLoading(false);
      }
    };

    checkAuth();
  }, []);

  const handleAuthClick = () => {
    setIsAuthModalOpen(true);
  };

  const handleCloseAuth = () => {
    setIsAuthModalOpen(false);
  };

  const handleLogout = () => {
    authService.logout();
    setUser(null);
  };

  return (
    <>
      <nav
        className={`navbar navbar-expand-lg navbar-light bg-light ${styles.navbar}`}
      >
        <div className="container">
          <Link className={`navbar-brand ${styles.navbarBrand}`} to="/">
            VolonteerHub
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <Link
                  className={`nav-link ${styles.navLink}`}
                  to="/organizations"
                >
                  Організації
                </Link>
              </li>
              <li className="nav-item">
                <Link className={`nav-link ${styles.navLink}`} to="/events">
                  Події
                </Link>
              </li>
              <li className="nav-item">
                <Link className={`nav-link ${styles.navLink}`} to="/profile">
                  Профіль
                </Link>
              </li>
              <li className="nav-item">
                <button
                  className={`btn btn-outline-primary ms-2 ${styles.authButton}`}
                  onClick={handleAuthClick}
                >
                  Увійти
                </button>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {isAuthModalOpen && <AuthForm onClose={handleCloseAuth} />}
    </>
  );
};

export default Navbar;
