import React from "react";
import Navbar from "../components/Navbar";
import EventCard from "../components/EventCard";
import "bootstrap/dist/css/bootstrap.min.css";

const HomePage: React.FC = () => {
  const events = [
    {
      title: "Прибирання парку",
      date: "15 травня 2024",
      location: "Парк Перемоги, Київ",
      description:
        "Допоможіть нам зробити наш парк чистішим та привабливішим для всіх відвідувачів.",
      imageUrl: "/photo_2024-04-18_02-14-36.jpg",
      tags: ["Екологія", "Прибирання", "Спільнота"],
      volunteersCount: 12,
    },
    {
      title: "Допомога безпритульним тваринам",
      date: "20 травня 2024",
      location: "Притулок для тварин, Львів",
      description:
        "Допоможіть нам доглядати за безпритульними тваринами та знаходити їм нові домівки.",
      imageUrl: "/photo_2024-04-18_02-14-36.jpg",
      tags: ["Тварини", "Допомога", "Волонтерство"],
      volunteersCount: 8,
    },
  ];

  return (
    <div>
      <Navbar />
      <div className="container mt-4">
        <div className="row">
          <div className="col-12 mb-4">
            <h1 className="text-center">Актуальні події</h1>
          </div>
        </div>
        <div className="row">
          {events.map((event, index) => (
            <div key={index} className="col-md-6 col-xl-4 mb-4">
              <EventCard {...event} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
